import React, { Component } from 'react';

//import logo from './logo.svg';

import './App.css';

class App extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',
  };

  componentDidMount() {
    //this.callApi()
      //.then(res => this.setState({ response: res.express }))
      //.catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };


  function(){
    (".validar").keydown(function(event){
        //alert(event.keyCode);
        if((event.keyCode < 48 || event.keyCode > 57) && (event.keyCode < 96 || event.keyCode > 105) && event.keyCode !==190  && event.keyCode !==110 && event.keyCode !==8 && event.keyCode !==9  ){
            return false;
        }
    });
  }


  handleSubmit = async e => {
    e.preventDefault();    
    var response = "";    
    var body = "";


        if (this.state.post == 'consultar'){
          if(this.state.identificador && (this.state.identificador >= 0 && this.state.identificador <= 99999999999999)){
            response = await fetch('/api/hello/'+this.state.identificador, Request);
            body = await response.text();    
          }else{
            body = "Debe ingresar un identificador valido";
          }
        }else if (this.state.post == 'grabar'){
          if(this.state.name && this.state.email){
              response = await fetch('/api/world', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                },
                body: JSON.stringify({name: this.state.name , email: this.state.email}),  
              });
              body = await response.text();
          }else if (!this.state.name && !this.state.email){
            body = "Debe ingresar un nombre y un correo electronico";
          }else if(!this.state.name){
            body = "Debe ingresar un nombre";
          }else{
            body = "Debe ingresar un email";
          }
        }

        this.setState({ responseToPost: body });


  };

render() {
    return (
      <div className="App">
        <header className="App-header">

          {/*
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          */}


        </header>
        <p>{this.state.response}</p>



        <form onSubmit={this.handleSubmit}>
          <table border="1" align="center" width = "100"> 
            <tr>
                <p><strong>Administrador de Usuarios:</strong></p>
                <br></br>
                <br></br>
                <p><strong>Crear usuario:</strong></p>
                Nombre de Usuario:
                <input
                  type="text"
                  name = "name"
                  value={this.state.nombre}
                  onChange={e => this.setState({ name: e.target.value })}
                />
                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                Correo Electronico:
                <input
                  type="text"
                  name = "email"
                  value={this.state.email}
                  onChange={e => this.setState({ email: e.target.value })}
                />
                <br></br>
                <br></br>
                <button  onClick={() => {this.setState({ post: "grabar"})}}>Grabar</button>
                <br></br>
                <br></br>

                <p>
                  <strong>Consultar usuario:</strong>
                </p>

                Identificador del Usuario:
                <input
                  type = "text"
                  name = "identificador"
                  value={this.state.identificador}
                  onChange={e => this.setState({ identificador: e.target.value })}
                />
                <br></br>
                <br></br>
                <button  onClick={() => {this.setState({ post: "consultar"})}}>Consultar</button>
                <br></br>
                <br></br>
                <br></br>
                <br></br>
                <p>{this.state.responseToPost}</p>
                <br></br>
                <br></br>
                <br></br>
                <input
                  type="text"
                  hidden= "true"
                  name="post"
                  value="2"
                  onChange={e => this.setState({ post: e.target.value })}
                />
                <br></br>
                <br></br>
                <br></br>
                <br></br>
                <br></br>
                <br></br>


                {/*<button type="submit" >Procesar</button>*/}




              </tr>
          </table>
        </form>
      </div>
    );
  }
}

export default App;
