import React, { Component } from 'react';

//import logo from './logo.svg';

import './App.css';

class App extends Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',
  };

  componentDidMount() {
    //this.callApi()
      //.then(res => this.setState({ response: res.express }))
      //.catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  handleSubmit = async e => {
    e.preventDefault();    

    var response = "";


    if (this.state.identificador){
      response = await fetch('/api/hello/'+this.state.identificador, Request);      
    }else{
      response = await fetch('/api/world', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({name: this.state.name , email: this.state.email}),  
      });
    }



    const body = await response.text();
    this.setState({ responseToPost: body });
  };

render() {
    return (
      <div className="App">
        <header className="App-header">

          {/*
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          */}
        </header>


        <p>{this.state.response}</p>



        <form onSubmit={this.handleSubmit}>
          <table border="1"> 
            <tr>
                <p>
                  <strong>Administrador de Usuarios:</strong>
                </p>

                <p>
                  <strong>Crear usuario:</strong>
                </p>

                Nombre de Usuario:
                <input
                  type="text"
                  name = "name"
                  value={this.state.nombre}
                  onChange={e => this.setState({ name: e.target.value })}
                />
                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                Correo Electronico:
                <input
                  type="text"
                  name = "email"
                  value={this.state.email}
                  onChange={e => this.setState({ email: e.target.value })}
                />


                <br></br>
                <br></br>
                <br></br>
                <br></br>

                <p>
                  <strong>Consultar usuario:</strong>
                </p>

                Identificador del Usuario:
                <input
                  type="text"
                  name = "identificador"
                  value={this.state.identificador}
                  onChange={e => this.setState({ identificador: e.target.value })}
                />

                <br></br>
                <p>El nombre del usuario es: {this.state.responseToPost}</p>
                <br></br>
                <br></br>
                <br></br>
                <input
                  type="text"
                  value={this.state.post}
                  onChange={e => this.setState({ post: e.target.value })}
                />


                <input
                  type="text"
                  name = "loco"
                  value={this.state.loco}
                  onChange={e => this.setState({ loco: e.target.value })}
                />

                <br></br>
                <br></br>
                <br></br>
                <br></br>
                <br></br>
                <br></br>


                <button type="submit" >Procesar</button>
              </tr>
          </table>
        </form>



        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}

export default App;
